data.positionLog.sort((e1, e2) => { return e1.timeStamp - e2.timeStamp });
let totalDistance = 0;
let totalTime = 0;
for(let i = 1;i < data.positionLog.length;i++) {
  let currPosition = data.positionLog[i];
  let prevPosition = data.positionLog[i-1];
  let diffX = currPosition.x - prevPosition.x;
  let diffY = currPosition.y - prevPosition.y;
  totalDistance += Math.sqrt((diffX * diffX) + (diffY * diffY));
  totalTime = currPosition.timeStamp;
}
return totalDistance / totalTime;
