var canvasElement = document.createElement('canvas');
targetElement.textContent = '';
targetElement.appendChild(canvasElement);
var ctx = canvasElement.getContext('2d');
var tmpDataset = [];
var tmpIncludeTitleArr = [];
for(var i = 0;i < data.eventLog.length;i++) {
  var str = data.eventLog[i].name + ': ' + JSON.stringify(data.eventLog[i].variables);
  if(!tmpIncludeTitleArr.includes(str)) {
    tmpIncludeTitleArr.push(str);
    tmpDataset.push({
      label: str,
      data: [],
      backgroundColor: color.getColor(str, 0.5),
      borderColor: color.getColor(str)
    });
  }
}
for(var i = 0;i < tmpDataset.length;i++) {
  for(var j = 0;j < data.eventLog.length;j++) {
  	var str = data.eventLog[j].name + ': ' + JSON.stringify(data.eventLog[j].variables);
    if(str === tmpDataset[i].label) {
      tmpDataset[i].data.push({
        x: data.eventLog[j].position.x,
        y: data.eventLog[j].position.y
      });
    }
  }
}

//Chart.js library
var scatterChart = new chart(ctx, {
  type: 'scatter',
  data: {
    labels: [],
    datasets: tmpDataset
  },
  options: {
    scales: {
      yAxes: [{
        ticks: {
          min: 0,
          max: 27,
          reverse: true
        }
      }],
      xAxes: [{
        ticks: {
          min: 0,
          max: 56 
        }
      }]
    }
  }
});
